    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int id = Integer.parseInt(cb.getSelectedItem().toString());
        DriverList d_l = new DriverList();
        DriverDetails d_details = d_l.getDriverDetails(id);
        d_name.setText(d_details.getD_name());
        d_phone.setText(d_details.getD_phone());
        Location loc = d_details.getCurrentLocation();
        int x = loc.getX();
        int y = loc.getY();
        d_c_x.setText("" + x);
        d_c_y.setText("" + y);
        d_status.setText("" 
                + ((d_details.getD_status()==0) ? "available" : "busy"));
        
    }//GEN-LAST:event_jButton1ActionPerformed

