    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int id = Integer.parseInt(cb.getSelectedItem().toString());
        DriverList d_l = new DriverList();
        DriverDetails d_details = d_l.getDriverDetails(id);
        if(r1.isSelected())
        {
            d_details.setD_status(0);
            if(d_l.getPassengerForDriver(id) != null)
            {
                PassengerList pl = new PassengerList();
                PassengerDetails pd = pl.getPassengerDetails(d_l.d_p.get(id));
                Location loc = pd.getDropLocation();
                d_details.setCurrentLocation(loc.getX(), loc.getY());
            }
            
        }
        else
        {
            d_details.setD_status(1);
        }
 
    }//GEN-LAST:event_jButton2ActionPerformed

